mapper.update:
UPDATE TB_MEMBER   
SET MEM_PW = #{ memPw }, MEM_MAIL = #{ memMail }, MEM_EMER_PHONE =#{ memEmerPhone },
 MEM_PHONE = #{ memPhone },MEM_ADDR1 = #{ memAddr1 }
 ,MEM_ADDR2 = #{ memAddr2 },MEM_ADDR3 = #{ memAddr3 }

WHERE MEM_ID=#{memId}

mapper.select:

   	   SELECT MEM_TOTAL_POINT
	   FROM TB_MEMBER
	   WHERE MEM_CODE=#{memCode}
